import csvresult=''with open(r"data-to-import/modules/groupedby.csv", "r", encoding="utf-8") as file:    csvreader=csv.reader(file)    for line in list(csvreader)[1:]:        line[0] = line[0].replace('\ufeff', '')        line[1] = line[1].replace('\ufeff', '')        result += f"""                <record id="{line[1]}" model="product.template">            <field name="name">{line[0]}</field>            <field name="detailed_type">consu</field>            <field name="categ_id" ref="product.product_category_all"/>            <field name="uom_id" ref="uom.product_uom_unit"/>            <field name="uom_po_id" ref="uom.product_uom_unit"/>            <field name="product_add_mode">matrix</field>        </record>"""with open(f'data-to-import/modules/product_template.txt','w',encoding="utf-8") as file_to_write:    file_to_write.write(result)print('completed!')